            sizeValueSet = true;
        }
        setShirtDetail({
            ...shirtDetail,
            size: e.target.value,
            sizeValueSet: sizeValueSet
        });
    };

    const HandleQuantityChange = (e) => {
        setShirtDetail({
            ...shirtDetail,
            quantity: e.target.value
        });
    };

    return (
        <div className=''>
