import React, { useEffect, useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import appRoutes from './shared/appRoutes';
import DefaultPages from './shared/DefaultPages';
import NoLayoutPages from './shared/NoLayoutPages';

import Products from './containers/Products/Products';
import Details from './containers/Details/Details';
import Home from './containers/Home/Home';
import NotImplement from './containers/NotImplement/NotImplement';
import Cart from './containers/Cart/Cart';
import LogIn from './containers/LogIn/LogIn';
import './App.css';
import { getAuth, signInWithPopup,signOut, GoogleAuthProvider} from "firebase/auth";
import { collection, getDocs, getFirestore, doc, setDoc, serverTimestamp, query, where, updateDoc} from "firebase/firestore/lite";

import firebase from "firebase/compat/app"
import "firebase/compat/auth"
import "firebase/compat/firestore"



function App(props) {

  const navigate = useNavigate();

  let cart = JSON.parse(localStorage.getItem("cart"));
  const CountQuantity = () => {
    if(cart!=null){
      return cart.reduce((sum, product) =>  sum = sum + parseInt(product.quantity), 0 );
    }
    else{
      return 0;
    }
  }
  const [totalQuantity, setTotalQuantity] = useState(CountQuantity());

  // Backend stuff

  useEffect(() => {
    document.title = 'SSUI';
    // getShoppingCartItem().then((d) => {
    //   let cart = d.docs.map((d) => d.data());
    //   console.log(cart);
    // }) 
  });
  const [currentUser, setCurrentUser] = useState(undefined); 
  const [userCart, setUserCart] = useState(undefined); 
  const db = getFirestore(props.app);
  const auth = getAuth(props.app);

  const provider = new GoogleAuthProvider();

  async function getShoppingCartItem(prop){
    const col = collection(db, "ShoppingCartItems");
    const q = query(col, where("userId", "==", prop.uid));
    return await getDocs(q);
  }

  function logIn(){
    signInWithPopup(auth, provider)
      .then((result) => {
        setCurrentUser(auth.currentUser);
        navigate(appRoutes.Home);

        getShoppingCartItem(auth.currentUser).then((d) => {
          let cart = d.docs.map((d) => d.data());
          setUserCart(cart);
        }) 
      })
      .catch((error)=> {})
  }

  function logOut(){
    signOut(auth).then(() => {
      setCurrentUser(undefined);
    }).catch((error) => {
      // An error happened.
    });
  }

  let cartItemRef = doc(collection(db, "ShoppingCartItems")); 

  function addToRomoteDb(cartItem){
    let newCartItem = {
      ...cartItem,
      userId: currentUser.uids,
      timeStamp: serverTimestamp()
    }
    setDoc(cartItemRef, newCartItem);;
  }


  return (
    <div className='App'>
        <Routes>
          <Route element={<DefaultPages totalQuantity = {totalQuantity} currentUser = {currentUser}/>}>
            <Route path={appRoutes.NotImplement} element={<NotImplement />} />
            <Route path={appRoutes.Home} element={<Home />} />
            <Route path={appRoutes.TShirts} element={<Products />} />
            <Route path={appRoutes.Details} 
                   element={<Details 
                                setTotalQuantity = {setTotalQuantity} 
                                totalQuantity = {totalQuantity} 
                                addToRomoteDb = {addToRomoteDb}
                                currentUser = {currentUser} />} />
            <Route path={appRoutes.Cart} 
                   element={<Cart 
                                setTotalQuantity = {setTotalQuantity} 
                                totalQuantity = {totalQuantity} 
                                userCart = {userCart}
                                currentUser = {currentUser}/>} />
            <Route path={appRoutes.LogIn} element={<LogIn logIn = {logIn} logOut = {logOut} currentUser = {currentUser}/>} />
          </Route>
        </Routes>
    </div>
  );
}

export default App;
