import React, { useEffect, useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import appRoutes from './shared/appRoutes';
import DefaultPages from './shared/DefaultPages';
import NoLayoutPages from './shared/NoLayoutPages';

import Products from './containers/Products/Products';
import Details from './containers/Details/Details';
import Home from './containers/Home/Home';
import NotImplement from './containers/NotImplement/NotImplement';
import Cart from './containers/Cart/Cart';
import LogIn from './containers/LogIn/LogIn';
import './App.css';
import { getAuth, signInWithPopup,signOut, GoogleAuthProvider} from "firebase/auth";
import { collection, getDocs, getFirestore } from "firebase/firestore/lite";



function App(props) {
  useEffect(() => {
    document.title = 'SSUI';
    // db.collection("anchors").get().then(querySnapshot => {
    //   console.log(querySnapshot.docs.map(doc => doc.data()))
    // })
  });
  const navigate = useNavigate();

  let cart = JSON.parse(localStorage.getItem("cart"));
  const [totalQuantity, setTotalQuantity] = useState(CountQuantity());
  const [currentUser, setCurrentUser] = useState(undefined); 

  const db = getFirestore(props.app);
  const auth = getAuth(props.app);

  const provider = new GoogleAuthProvider();

  async function getTodos(col){
    return await getDocs(col);
  }

  function logIn(){
    signInWithPopup(auth, provider)
      .then((result) => {
        console.log("login");
        setCurrentUser(auth.currentUser);
        navigate(appRoutes.Home);
      })
      .catch((error)=> {})
  }

  function logOut(){
    signOut(auth).then(() => {
      setCurrentUser(undefined);
      console.log("logout");
    }).catch((error) => {
      // An error happened.
    });
  }
  const CountQuantity = () => {
    if(cart!=null){
      return cart.reduce((sum, product) =>  sum = sum + parseInt(product.quantity), 0 );
    }
    else{
      return 0;
    }
  }

  return (
    <div className='App'>
        <Routes>
          <Route element={<DefaultPages totalQuantity = {totalQuantity} currentUser = {currentUser}/>}>
            <Route path={appRoutes.NotImplement} element={<NotImplement />} />
            <Route path={appRoutes.Home} element={<Home />} />
            <Route path={appRoutes.TShirts} element={<Products />} />
            <Route path={appRoutes.Details} element={<Details setTotalQuantity = {setTotalQuantity} totalQuantity = {totalQuantity}/>} />
            <Route path={appRoutes.Cart} element={<Cart setTotalQuantity = {setTotalQuantity} totalQuantity = {totalQuantity}/>} />
            <Route path={appRoutes.LogIn} element={<LogIn logIn = {logIn} logOut = {logOut} currentUser = {currentUser}/>} />
          </Route>
        </Routes>
    </div>
  );
}

export default App;
