import React, { useState, useEffect} from 'react';
import './Cart.css';
import ImageTest from '../../assets/shirt_images/beepboop-blue-front.png';
import {default as Shirts} from '../../shared/shirts';
import {default as shirtSizes} from '../Details/ShirtSize';
import CartSummary from './CartSummary';

const Cart = props => {
    // let cart = undefined;
    // if (props.currentUser == undefined){
    //     cart = JSON.parse(localStorage.getItem("cart"));
    //     if (cart!=null) {
    //         cart.reverse();
    //     }
    //     console.log(cart);
    // }
    // else {
    //     cart = props.userCart;
    //     console.log(cart);
    // }

    const CountTotal = () => {
        if (props.cart!=null) {
            return props.cart.reduce((sum, product) =>  sum = sum + parseFloat(product.price.slice(1)) * parseInt(product.quantity), 0 ).toFixed(2);      
        }
        else {
            return 0;
        }
    };

    const CountQuantity = () => {
        if(props.cart!=null){
            return props.cart.reduce((sum, product) =>  sum = sum + parseInt(product.quantity), 0 );
        }
        else{
            return 0;
        }
    }

    const [CartTotalValues, setCartTotalValues] = useState({
        Subtotal: CountTotal(),
        QuantityTotal: CountQuantity()
    });

    const HandleQuantityChange = (e, index) => {
        props.cart[index].quantity = e.target.value;
        setCartTotalValues({
            Subtotal: CountTotal(),
            QuantityTotal: CountQuantity()
        });
        if(props.currentUser == undefined){
            SetLocalStorage();
        }
        else{
            props.updateDbCart(props.cart[index].id, Object.assign({}, props.cart));
        }
        props.setTotalQuantity(CountQuantity());
        //document.getElementById("cart-count").innerHTML = CountQuantity();
    }

    const HandleSizeChange = (e, index) => {
        props.cart[index].size = e.target.value;
        setCartTotalValues({
            Subtotal: CountTotal(),
            QuantityTotal: CountQuantity()
        });
        if(props.currentUser == undefined){
            SetLocalStorage();
        }
        else{
            props.updateDbCart(props.cart[index].id, Object.assign({}, props.cart));
        }

    };

    const HandleColorChange = (e, index) => {
        props.cart[index].shirtColor = e.target.value;
        props.cart[index].shirtDetailImg = Shirts[props.cart[index].productIndex].colors[e.target.value]["front"];
        setCartTotalValues({
            Subtotal: CountTotal(),
            QuantityTotal: CountQuantity()
        });
        if(props.currentUser == undefined){
            SetLocalStorage();            
        }
        else{
            props.updateDbCart(props.cart[index].id, Object.assign({}, props.cart));
        }
    };

    const HandleRemove = (index) => {
        props.cart.splice(index, 1);
        setCartTotalValues({
            Subtotal: CountTotal(),
            QuantityTotal: CountQuantity()
        });
        if(props.currentUser == undefined){
            SetLocalStorage();           
        }
        else{
            props.removeDbCart(cart[index].id);
        }
        props.setTotalQuantity(CountQuantity());
    }

    const SetLocalStorage = () => {
        localStorage.setItem('cart', JSON.stringify(cart.reverse()));
        cart.reverse();
    }

    return (
        <div className="product-container products-section" >
            <h1 id="product-name">My Cart (<span id='titleCartCount'>{CartValues.QuantityTotal}</span>)</h1>
            <div className='cart-content-box'>
                <div className='cart-product-div'>
                    { 
                        (() => {
                            if (CartValues.QuantityTotal === 0 ) {
                                return( <h2 id='cartEmptyTxt'>Your Cart is Empty.</h2>);
                            }
                            else {
                                let products = [];
                                return CartValues.cart.map((product, index) => {
                                    return (
                                        <div className='cart-product-box'>
                                            <h2 className='cart-product-name'>{product.name}</h2>
                                            <div className='cart-detail-content'>
                                                <img src={product.shirtDetailImg} className='cart-product-img'></img>
                                                <div className='cart-detail-box'>
                                                    <div className='cart-detail'>
                                                        <p>Quantity: </p>
                                                        <select name="quantity" className="quantity" value={product.quantity} onChange={ (e) => HandleQuantityChange(e, index)} >
                                                        {
                                                            (() => {
                                                                let options = [];
                                                                for(let i = 1; i < 21; i++){
                                                                    options.push(<option value={i}>{i}</option>);
                                                                }
                                                                return options;
                                                            })()
                                                        }
                                                        </select>
                                                    </div>
                                                    <div className='cart-detail'>
                                                        <p>Color:</p>
                                                        <select name="color" className="color" value={product.shirtColor} onChange={ (e) => HandleColorChange(e, index)} >
                                                        {
                                                            Object.entries(Shirts[product.productIndex].colors).map(([key, val]) => {
                                                                return (
                                                                    <option value={key}>{key}</option>
                                                                )     
                                                            })
                                                        }
                                                        </select>
                                                    </div>
                                                    <div className='cart-detail'>
                                                        <p>Size:</p>
                                                        <select name="size" className="size" value={product.size} onChange={ (e) => HandleSizeChange(e, index)}>
                                                        {
                                                            shirtSizes.map((size, index) => {
                                                                return (<option value={size} className="sizeOption">{size}</option>);
                                                            })
                                                        }
                                                        </select>
                                                    </div>
                                                    <div className='cart-detail'>
                                                        <p>Price (each):</p>
                                                        <p className='price'>{product.price}</p>
                                                    </div>
                                                    <div className="cart-button cart-product-button" onClick={ () => HandleRemove(index)}>
                                                        Remove
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    );
                                })

                                return products;
                            }                                
                        })()                       
                    } 

                </div>

                {
                    <CartSummary subtotal = {CartValues.Subtotal}/>
                }
            </div>
        </div>
    );
};

export default Cart;
