import React, { useState, useEffect } from 'react';
import appRoutes from '../../shared/appRoutes';

import GoogleImg from '../../assets/images/google-logo.png';
import app from '../../index';
import './LogIn.css';

const NavBar = (props) =>  {
    const [isLogIn, setIsLogInValue] = useState(false);

    function LogInButton(props) {
        return (
            <button className='logInBtn' id='logInBtn' onClick={() => props.logIn()}>
                <img src={GoogleImg} id="google-logo"/>
                <p>Log In with Google</p>
            </button>
        );
    }

    function LogOutButton() {
        return (
            <button className='logInBtn' id='logOutBtn'>
                <img src={GoogleImg} id="google-logo"/>
                <p>Log Out with Google</p>
            </button>  
        );
    }

    return (
        <div className='logInContainer'>
            {isLogIn
                ? <LogOutButton />
                : <LogInButton onClick={() => props.logIn} />
            }
        </div>     
    )
}
export default NavBar;
